func TestTree(t *testing.T) {

	toc := make([]yuqueg.RepoTocData, 0)
	toc = append(toc, yuqueg.RepoTocData{
		Id:         1,
		Title:      "t1",
		UUID:       "t1",
		ParentUUID: "",
	}, yuqueg.RepoTocData{
		Id:         2,
		Title:      "t2",
		UUID:       "t2",
		ParentUUID: "",
	}, yuqueg.RepoTocData{
		Id:         3,
		Title:      "t1-t3",
		UUID:       "t3",
		ParentUUID: "t1",
	}, yuqueg.RepoTocData{
		Id:         4,
		Title:      "t1-t2-t4",
		UUID:       "t4",
		ParentUUID: "t2",
	}, yuqueg.RepoTocData{
		Id:         5,
		Title:      "t1-t2-t4-t5",
		UUID:       "t5",
		ParentUUID: "t4",
	}, yuqueg.RepoTocData{
		Id:         6,
		Title:      "t1-t2-t4-t6",
		UUID:       "t6",
		ParentUUID: "t4",
	})
	res := treeify(toc)
	assert.Equal(t, res[0].Id, "t1")
	b, err := json.Marshal(res)
	if err != nil {
		t.Fail()
	}
	fmt.Printf("%s", string(b))
}

